import React, { useState, useEffect } from 'react';
const Search = "https://api.themoviedb.org/3/search/movie?&api_key=38d0409f65543e3dd6d22adbccdefa20&query="
const Header = () => {
    const [searchTerm, setsearchTerm] = useState('')
    const submitBtn = (e) =>{
        e.preventDefault();
        fetch(Search + searchTerm).then(res => res.json()).then(data => console.log(data.results))
    }
    const handlerBtn = (e) => {
        setsearchTerm(e.target.value)
    }
    return ( 
        <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light p-3">
  <a className="navbar-brand" href="#"><img className="logo" src="./logo.png"></img></a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span className="navbar-toggler-icon"></span>
  </button>

  <div className="collapse navbar-collapse" id="navbarSupportedContent">
    <form onSubmit={submitBtn} className="form-inline my-2 my-lg-0">
      <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
      <button onChange={handlerBtn} className="btn btn-primary my-2 my-sm-0" type="submit">Search</button>
    </form>
  </div>
</nav>
        </>
     );
}

export default Header;
