import React, { Component } from 'react';
import axios from 'axios';
import Loading from './Loading';
import { Link } from 'react-router-dom';

class Users extends React.Component {
    state = {
        users : [],
        isLoading : true
    }
    async componentDidMount(){
        const response = await axios.get('https://reqres.in/api/users/');
         this.setState({users : response.data.data, isLoading : false});

    }

    render() { 
        return (
            <>
                <div className="container">
                        <button onClick={this.handleCreate} className="btn btn-primary">Create</button>
                        <div className="row">
                            {
                                this.state.Loading ? (
                                    <Loading />

                                ) : (

                                        this.state.users.map((user) => {
                                            return(
                                                <div className="col-md-4 text-center p-5"> 
                                                    <img src={user.avatar} style={{borderRadius : '50%' , width : '200px'}} alt=""/>
                                            <Link to={`/${user.id}`}> 
                                                    <h4>{user.first_name} {user.last_name}</h4>
                                            </Link>
                                                    <h5>{user.email}</h5>
                                                <div className="row">
                                                        <div className="col-md-6"> 
                                                            <button onClick={() => this.handleUpdate(user)} className="btn btn-success btn-sm">Update</button>
                                                        </div>
                                                        <div className="col-md-6"> 
                                                            <button onClick={() => this.handleDelete(user)} className="btn btn-danger btn-sm">Delete</button>
                                                        </div>
                                                </div>
                                                    </div>
                                            )
                                        })

                                )
                            }
                            </div> 
                    </div>
            </>
        );
    }
    handleCreate = async () => {
        const newUser = {
            first_name : 'ana',
            last_name : 'rahimi',
            email : 'anarahimi@gmail.com',
            avatar : 'https://www.faceapp.com/static/img/content/compare/impression-example-before@3x.jpg'
        }
        const resp = await axios.post('https://reqres.in/api/users/');
        this.setState({users : [...this.state.users, newUser]})
    }
    handleUpdate = async (user) => {
        user.first_name = 'updated';
        const respons =  await axios.put('https://reqres.in/api/users/',user)
        console.log(respons);
        const updatedUsers = [...this.state.users];
        this.setState({users : updatedUsers});

    }
    handleDelete = async(user) => {
        const ress = await axios.delete('https://reqres.in/api/users/');
        const newacc = this.state.users.filter(item => item.id !== user.id)
        this.setState({users : newacc})
    }
}

export default Users;
