import React, { useState } from 'react';
const Adduser  = (props) => {
    const [newusername, setusername] = useState('');
    const [newage, setage] = useState('');
    const subHandeler = (e) => {
        e.preventDefault();
        if(newusername.value == "" || newage.value == ""){
            alert('مقادیری وارد نشده است')
        }else if(newage.value < 0){
            alert('سن قابل قبول نیست')
        }
        // console.log(newusername,newage)
    }
    const usernameChange = (e) =>{
        setusername(e.target.value)
    }
    const ageChange = (e) =>{
        setage(e.target.value)
    }

    return ( 
        <>
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-6 offset-3">
                    <form onSubmit={subHandeler} className="form-group">
                         <label className="text-secondary m3" htmlFor="username">Username</label>
                          <input onChange={usernameChange} id="username" type="text" className="form-control"></input>
                           <label className="text-secondary m3" htmlFor="age">Age</label>
                           <input onChange={ageChange} id="age" type="number" className="form-control"></input>
                            <button className="btn btn-success mt-3">Add To List</button>
                     </form>    
                </div>           
            </div>
        </div>
        </>
     );
}

export default Adduser ;
